        private async void HowToBtnTappedAsync(object obj)
        {
            //isRunning = false;
            //Counting();
            //await App.Current.MainPage.DisplayAlert("Time", (TimeDisplayed), "ok");
            await App.Current.MainPage.DisplayAlert("How to:", "Start by picking the right weight for the exercise (if it requires any extra weight). You should pick weight based on your strengh, it should be challenging enough to make you push yourself while also being low enough so you can complete your sets and reps. For instance if you pick 50kg (110lbs) and the exercise says 3 x 8, then you should lift the weight 8 times in a row and repeat it 3 times. A good indication of the right weight is if you only have 1 or 2 more reps in the tank on your last set.", "OK");
            //isRunning = true;
        }
        private async void Counting()
        {
            isRunning = true;

            while (isRunning)
            {
                await Task.Delay(1000); // Wait 1 second
                theTimeCounter = theTimeCounter.Add(TimeSpan.FromSeconds(1));
                SetTime();
                // Ensure the UI updates on the main thread
                await MainThread.InvokeOnMainThreadAsync(() =>
                {
                    //TimeDisplayed = theTimeCounter.ToString(); // This triggers OnPropertyChanged()
                    TimeDisplayed = theTime;
                    //App.Current.MainPage.DisplayAlert("Time", (TimeDisplayed), "ok");
                });
            }
        }
        private void SetTime()
        {
            theTime = $"{theTimeCounter.Hour}:{theTimeCounter.Minute}:{theTimeCounter.Second}"; 
        }
